// Helper function for SEO recommendations
function generateSEORecommendations(metrics: any[]): string[] {
  const recommendations: string[] = []
  
  metrics.forEach((metric: any) => {
    if (metric.avg_lcp > 2500) {
      recommendations.push(`${metric.url}: LCP yava≈ü (${metric.avg_lcp}ms) - g√∂rselleri optimize edin`)
    }
    if (metric.avg_cls > 0.1) {
      recommendations.push(`${metric.url}: Layout shift y√ºksek (${metric.avg_cls}) - CSS layout'unu d√ºzeltin`)
    }
    if (metric.avg_fcp > 1800) {
      recommendations.push(`${metric.url}: FCP yava≈ü (${metric.avg_fcp}ms) - CSS ve JS optimize edin`)
    }
  })
  
  return recommendations.slice(0, 5) // Top 5 recommendations
}

