// KVKV Data Subject Rights API (Veri Sahibi Haklarƒ±)
app.post('/api/privacy/data-subject-request', async (c) => {
  const { DB } = c.env
  
  try {
    const { 
      requestType, 
      firstName,
      lastName,
      email,
      phoneNumber,
      identityNumber,
      requestDetails,
      documentUrls 
    } = await c.req.json()
    
    if (!requestType || !firstName || !lastName || !email) {
      return c.json({ error: 'Zorunlu alanlar eksik' }, 400)
    }
    
    const validRequestTypes = [
      'data_access',        // Veri eri≈üim talebi
      'data_correction',    // Veri d√ºzeltme talebi
      'data_deletion',      // Veri silme talebi
      'data_portability',   // Veri ta≈üƒ±nabilirlik
      'processing_restriction', // ƒ∞≈üleme sƒ±nƒ±rlama
      'objection_to_processing' // ƒ∞≈ülemeye itiraz
    ]
    
    if (!validRequestTypes.includes(requestType)) {
      return c.json({ error: 'Ge√ßersiz talep t√ºr√º' }, 400)
    }
    
    // Generate unique request ID\n    const requestId = 'DSR-' + Date.now() + '-' + Math.random().toString(36).substring(7).toUpperCase()
    
    // Log data subject request
    const result = await DB.prepare(`
      INSERT INTO kvkv_data_subject_requests (
        request_id,
        request_type,
        first_name,
        last_name,
        email,
        phone_number,
        identity_number,
        request_details,
        document_urls,
        status,
        ip_address,
        user_agent,
        created_at
      ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
    `).bind(
      requestId,
      requestType,
      firstName,
      lastName,
      email,
      phoneNumber || null,
      identityNumber || null,
      requestDetails || '',
      JSON.stringify(documentUrls || []),
      'pending',
      c.req.header('CF-Connecting-IP') || 'Unknown',
      c.req.header('User-Agent') || 'Unknown',
      new Date().toISOString()
    ).run()
    
    // Send confirmation email (would be implemented with SendGrid)
    // await sendDataSubjectRequestConfirmation(email, requestId, requestType)
    
    return c.json({
      success: true,
      requestId: requestId,
      message: 'Veri sahibi talebi ba≈üarƒ±yla kaydedildi',
      estimatedResponseTime: '30 g√ºn i√ßinde'
    })
    
  } catch (error) {
    console.error('Data subject request error:', error)
    return c.json({ error: 'Veri sahibi talebi i≈ülenemedi' }, 500)
  }
})

